/**
 * Prettify & simplify GraphQL query/mutation/subscription.
 * https://github.com/Emetrop/graphql-prettier
 */
declare module "graphql-prettier" {
  /**
   * Prettify & simplify GraphQL query/mutation/subscription.
   *
   * @param source is a string with GraphQL query/mutation/subscription to prettify
   * @param noDuplicates is a boolean value which set up to false turns out function to leaving duplicated fields
   * @returns Prettified query as string
   */
  // eslint-disable-next-line unused-imports/no-unused-vars
  const gqlPrettier = (source: string, noDuplicates = true) => string;

  export default gqlPrettier;
}
